import React from "react";
import Wrapper from "../Wrapper/Wrapper";
import FriendsCard from "../FriendsCard/FriendsCard";
import friends from "../../friends.json"
import Scoreboard from "../Scoreboard/Scoreboard.js";

class Game extends React.Component {

  state = {
    showAlert: 0,
    showSuccess: 0,
    score: 0,
    topScore: 0,
    friends,
    clickeImagesArray: []
  };

  clickedImage = id => {
    let clickeImagesArray = this.state.clickedImagesArray;
    let score = this.state.score;
    let topScore = this.state.topScore;
    this.setState({ 
      showAlert: 0
    });

   // if the clicked image has an id of the indexed paintings
   if (clickeImagesArray.indexOf(id) === -1) {
    // push that id into that id into the array to be stored
    clickeImagesArray.push(id);
    console.log(clickeImagesArray);
    // run the score function
    this.handleIncrement();
    // run the reshuffle function after each click
    this.makeShuffle();
  } else if (this.state.score === 12) {
    // alert player wins
    this.setState({
      showSuccess: 1,
      score: 0,
      clickeImagesArray: []
    });
  } else {
     // alert player loss
    this.setState({
      score: 0,
      clickeImagesArray: []
    });
    console.log("duplicate");
    this.setState({
      showAlert: 1
    });
  }

  if (score > topScore) {
    this.setState({
      topScore: score
    });
  }
};


  handleIncrement = () => {

    this.setState({ score: this.state.score + 1 });
  };

  render() {
    return (
      <div>
        <Scoreboard
          title="Simpsons Clicky Game"
          score={this.state.score}
          topScore={this.state.topScore}
        />

      <Wrapper>

        {this.state.friends.map(friend => (
          <FriendsCard
            id={friend.id}
            key={friend.id}
            name={friend.name}
            image={friend.image}

          />
        ))}
      </Wrapper>
        </div>
      );
  }
}

export default Game;
